mapper.select:
SELECT count(*)
FROM USERS
WHERE upper(USER_ID) = upper(#{userId}) AND USER_PW=#{userPw}

SELECT ROLE
FROM USERS
WHERE upper(USER_ID)=upper(#{userId}) AND USER_PW=#{userPw}

SELECT USERNO
FROM USERS
WHERE EMAIL=#{email} and USER_ID=#{userId}

SELECT count(*)
FROM USERS
WHERE USER_ID=#{userId}

SELECT USER_Id
FROM USERS
WHERE USER_Name=#{userName} and EMAIL=#{email}

SELECT count(*)
FROM USERS
WHERE STATUS='block' and USER_Id=#{userId}

SELECT count(*)
FROM USERS
WHERE STATUS='leave' and USER_Id=#{userId}
